def leaky_integrate_and_fire(V0, I, tau, dt, T):
    V = V0
    times = np.arange(0, T, dt)
    voltages = []

    for t in times:
        dV = (-V / tau + I) * dt
        V += dV
        if V >= threshold:
            V = 0  # reset after firing
        voltages.append(V)

    return times, voltages

